import { GET_CHAT_FAILURE } from "../components/constants/ActionTypes";
import { GET_CHAT_REQUEST } from "../components/constants/ActionTypes";
import { GET_CHAT_SUCCESS } from "../components/constants/ActionTypes";
import { GET_MESSAGE_FAILURE } from "../components/constants/ActionTypes";
import { GET_MESSAGE_REQUEST } from "../components/constants/ActionTypes";
import { GET_MESSAGE_SUCCESS } from "../components/constants/ActionTypes";
const getChatSuccess = (chat) => ({
    type: GET_CHAT_SUCCESS,
    payload: chat,
})

const getChatStarted = () => ({
    type: GET_CHAT_REQUEST
})

const getChatFailure = (message) => ({
    type: GET_CHAT_FAILURE,
    payload: message
})

export const getChat = () => {
    return ((dispatch, getState) => {
        dispatch(getChatStarted())
        fetch('http://127.0.0.1:7000/chats/list_add/')
            .then(response => response.json())
            .then(response => {dispatch(getChatSuccess(response))})
            .catch(err => dispatch(getChatFailure(err.message)));
        })
}

const getMessageSuccess = (message) => ({
    type: GET_MESSAGE_SUCCESS,
    payload: message,
})

const getMessageStarted = () => ({
    type: GET_MESSAGE_REQUEST
})

const getMessageFailure = (message) => ({
    type: GET_MESSAGE_FAILURE,
    payload: message
})

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.autoplay = true;
}

function update_back_chat(chat, id, centrifuge, dispatch){   
    const sub = centrifuge.newSubscription(id.toString());
    sub.on('publication', function(ctx) {dispatch(getMessageSuccess({'chat':chat, 'content':[ctx.data]}))
                                         if((window.location.hash != '#/')&&(window.location.hash != '')){
                                            if(window.location.href.split('_')[1]!=id.toString()){
                                                new Notification('New message '+ctx.data["sender"]+': '+ctx.data["content"]);
                                                beep();
                                            }
                                        }
    });
    sub.subscribe();
    centrifuge.connect();
}

function update_common_chat(chat, sse, dispatch){
    sse.onopen = function() {};
    sse.onmessage = function(e) {let opType;
                                 let time;
                                 let msg;
                                 let date_send;
                                 let date;
                                 try {
                                    msg = JSON.parse(e.data);
                                    opType = msg.operationType;
                                    time = msg.timestamp;
                                    date_send = new Date( Date.parse(time.substr(0, time.length-1)) );
                                    date_send.setSeconds(0);
                                    date_send.setMilliseconds(0);
                                    date_send = date_send.getTime();
                                    date = new Date();
                                    date.setHours(date.getHours() - 3);
                                    date.setSeconds(0);
                                    date.setMilliseconds(0);
                                    date = date.getTime();
                                } catch {
                                    console.log(e.data)
                                    opType = null
                                 }
                                 let raz = date_send - date;
                                 if ((!opType || opType === 'insert') && (-3000 <= raz) && (raz <= 30000)) {
                                    dispatch(getMessageSuccess({'chat':chat, 'content':[msg]}));
                                    if(window.location.hash != '' && window.location.hash != '#/'){
                                        if(window.location.hash != '#/chat/tt-front_tt-front'){
                                            new Notification('New message '+msg.author+': '+msg.text);
                                            beep();
                                        }
                                    }                            
                                 }
    };
    sse.onerror = function() {
        console.log('EventSource failed.');
    };
}

export const getMessage = (address, chat, id, connection) => {
    return ((dispatch, getState) => {
        dispatch(getMessageStarted())
        fetch(address)
            .then(response => response.json())
            .then(response => {dispatch(getMessageSuccess({'chat':chat, 'content':response}))
                               if(id != "tt-front"){
                                   update_back_chat(chat, id, connection, dispatch)
                               }else{
                                   update_common_chat(chat, connection, dispatch)
                               }})
            .catch(err => dispatch(getMessageFailure(err.message)));
        })
}